import React from 'react';
import "./controller.scss"

const buildControl = ( props ) => {
    return (
        <div className="build-controller">
            <h3>{props.ingredient}</h3>
            <button className="control-button"><span className="fas fa-plus fa-lg"></span></button>
            <button className="control-button"><span className="fas fa-minus fa-lg"></span></button>
        </div>
    )

};

export default buildControl;
