import React from 'react';
import "./controller.scss"

const buildControl = ( props ) => {
    return (
        <div className="build-controller">
            <h4>{props.ingredient}</h4>
            <div className="controller__buttons-wrapper">
                <div>
                    <p><strong>Quantity:</strong> {props.quantity}</p>
                </div>
                <div>
                    <button onClick={props.handleIncreaseIngredient().bind(this, props.key)} className="control-button"><span className="fas fa-plus fa-lg"></span></button>
                    <button onClick={props.handleDecreaseIngredient().bind(this, props.key)} className="control-button"><span className="fas fa-minus fa-lg"></span></button>
                </div>
            </div>
        </div>
    )

};

export default buildControl;
