import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';
import ValidationComponent from './ValidationExercise/ValidationComponent';
import CharComponent from './ValidationExercise/CharComponent';

class App extends Component {

  state = {
    message: "",
    length: 0
  };

  changeHandler = (event) => {
    this.setState({
       message: event.target.value,
       length: event.target.value.length,
    });
  };

  textArray = ("" + this.state.message).split('');
  render() {
    console.log(this.textArray);
    return (
      <div className="App">
          <input type="text" placeholder="enter your text..." onChange={this.changeHandler}/>
          <p>{this.state.length}</p>
          <h4>Validator:</h4>
          <ValidationComponent length={this.state.length} />
          <hr/>
          <h4>Output Text:</h4>
          <CharComponent letter="j" />
      </div>
    );
  }
}

export default App;
