import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Person from './Person/Person';
import UserOutput from './UserForm/UserOutput';
import UserInput from './UserForm/UserInput';


class App extends Component {

  state = {
    persons: [
        { name: "Niv", age: 25 },
        { name: "Prague", age: 22 },
        { name: "Roie", age: 26 },
    ]
  };

   personsList = () => {
    return (
        <div>
            {this.state.persons.map(m => {
                return <Person name={m.name} age={m.age} />
            })}
        </div>
    );
  }

  render() {
    return (
      <div className="App">
          { this.personsList }
      </div>
    );
  }
}

export default App;
