import React from 'react';
import "./Backdrop.scss";

const backdrop = (props) => (
    props.show ? <div className="backdrop"
                      style={{opacity: props.show ? "1" : "0"}}
                      onClick={props.hideModal} /> : null
);

export default backdrop;
