import React from 'react';
import Ingredient from "./Ingredient"
import "./IngredientsClasses.scss"

const burgerPreview = ( props ) => {
    const ingredientList = ["topBun",...props.ingredients, "bottomBun"];
    const preview = ingredientList.map((p, index) => (
        <Ingredient ingredient={p.name} key={index} />
    ));
    return (
      <div className="burger-preview-wrapper">
          <Ingredient ingredient={"Meat".toLowerCase()} />
      </div>
    );
};

export default burgerPreview;
