import React from 'react';

const charComponent = ( props ) => {
    const style = {
        display: "inline-block",
        padding: "10px",
        textAlign: "center",
        margin: "1px",
        border: "1px solid #ccc",
        backgroundColor: "#ddd",
        color: "#fff",
        borderRadius: "4px",
        cursor: "pointer",
        transition: "all .2s ease-out",
        ":hover": {
            backgroundColor: "#ccc"
        }
    };

    return (
        <div style={style} onClick={props.click}>
            {props.letter}
        </div>
    );
};

export default charComponent;
