import React from 'react';
import './validators.scss';

const validationComponent = ( props ) => {
    const style = {
        padding: "10px",
        border: "none",
        backgroundColor: "#af3b41",
        color: "#fff",
        transition: "all .3s ease-out",
    };
    let validation = "";

    if (props.length > 5) {
        validation = "Text long enough";
        style.backgroundColor = "#23af54";
    }
    else {
        validation = "Text too short";
        style.backgroundColor = "#af3b41";
    }

    return (
        <div className="alert">
            <p>{validation}</p>
        </div>
    );
};

export default validationComponent;
