import React from 'react';
import "./Summary.scss";

const orderModal = (props) => {
    const orderDetails = props.orderDetails.ingredients.map((n, i) => (
       <li>{n.name + "(" + n.quantity + ")"}</li>
    ));
    return (
        <div className="order-modal-wrapper">
            <div className="overlay"/>
            <div className="modal-window">
                <div className="modal-header">
                    <h4 className="modal-title">Your Order</h4>
                    <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                        <span aria-hidden="true">&times;</span>
                    </button>
                </div>
                <div className="modal-body">
                    {orderDetails}
                    <p><strong>{"Total price: " + props.sum.toLocaleString('en-US', { style: 'currency', currency: 'USD' })}</strong></p>
                </div>
                <div className="modal-footer">
                    <button type="button" className="btn btn-secondary" data-dismiss="modal">Not now</button>
                    <button type="button" className="btn btn-success">Proceed to Checkout</button>
                </div>
            </div>
        </div>
    );
};

export default orderModal;
