import React from 'react';
import Radium from 'radium';

const validationComponent = ( props ) => {
    const style = {
        padding: "10px",
        border: "none",
        backgroundColor: "#af3b41",
        color: "#fff",
        transition: "all .2s ease-out",
        ":hover": {
            fontSize: "1.3rem",
        }
    };
    let validation = "";

    if (props.length > 5) {
        validation = "Text long enough";
        style.backgroundColor = "#23af54";
    }
    else {
        validation = "Text too short";
        style.backgroundColor = "#af3b41";
    }



    return (
        <div style={style}>
            <p>{validation}</p>
        </div>
    );
};

export default validationComponent;
